den_myIdx s mycm.hidden="true" }else{ ;s mycm.header=$s(myColumnCaption'="":myColumnCaption,1: myColumnName) ;wanghc 2012-10-31 国际化 s mycm.header=$s(myColumnCaption'="":"$g('"_myColumnCaption_"')",1: "$g('"_myColumnName_"')") ;"$g('"_myColumnName_"')" ; s mycm.dataIndex=myColumnName i (uSet'=""),$g(hiddenPlist(myIdx))="" s mycm.hidden="true" i +myColumnWidth>0 s mycm.width=+myColumnWidth i ($g(hiddenPlist(myIdx))=1),$g(hiddenPlist(myIdx,2))'="" s mycm.width=hiddenPlist(myIdx,2) } d mycms.cms.Insert(mycm) s myfn=##class(ext.entity.Field).%New() s myfn.name=mycm.dataIndex ;;myColumnName d mycms.fns.Insert(myfn) s ^||MyColTempList(myIdx)=1 }
s mycmsNew=##class(ext.entity.GridModal).%New() ;把显示的顺序弄好 i uSet'="" d .f idx=1:1:$l(uSet,"|") d ..s uSetItem = $p(uSet,"|",idx) ..s myIdx = $p(uSetItem,",",1) ;col-index,width,sort| ..q:+myIdx=0 ..d mycmsNew.cms.Insert(mycms.cms.GetAt(myIdx)) ..d mycmsNew.fns.Insert(mycms.fns.GetAt(myIdx)) ..k ^||MyColTempList(myIdx) ;把最后面隐藏的加上 s idx="" f s idx = $o(^||MyColTempList(idx)) q:idx="" d .d mycmsNew.cms.Insert(mycms.cms.GetAt(idx)) .d mycmsNew.fns.Insert(mycms.fns.GetAt(idx)) d mycms.cms.%Close() d mycms.fns.%Close() s mycms.cms = "" s mycms.fns = "" d mycms.%Close() s mycms="" s myjosnstr =mycmsNew.GetJosnData() i $g(pageSize)'="" s myjosnstr = $e(myjosnstr,1,$l(myjosnstr)-1)_",pageSize:"_pageSize_"}"
